import React, { useEffect, useState } from 'react'
import { useNavigate, useParams } from 'react-router-dom'
import { Avatar, Box, Button, Card, CardActions, CardContent, Checkbox, CssBaseline, Drawer, List, ListItem, ListItemButton, ListItemText, TextField, Typography } from '@mui/material'
import DoneIcon from '@mui/icons-material/Done';
import MyAppBar from './MyAppBar';
import { getToken } from '../utilities/LoginService';
import SideMenu from './SideMenu';


const SelectMilestone = ({nextStage, setMilestoneToAward, projectId, userToAward}) => {

  const [project, setProject] = useState(null)
  useEffect(()=>{
    fetch("http://localhost:8000/projects/"+projectId,
    {
      mode: "cors",
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": getToken(), 
        "Access-Control-Allow-Origin": "*"
      },
    })
    .then((res)=>{
      if(res.ok)
      {
        return res.json()
      }
    })
    .then((data)=>{
      console.log("Project")
      console.log(data)
      setProject(data)
    })
  },[projectId,])

  const navigate = useNavigate()
  const [milestone, setMilestone] = useState("")

  return (
    <Card sx={{width:500}}>
      <CardContent>
        <Typography component="h1" variant="h5">
          Select Milestone To Award
        </Typography>
        {(project)?
        <List>
          {project.milestones.filter((m)=>(!isMilestoneAchieved(user))).map((val=>(
            <ListItem key={val._id} secondaryAction={
              <Checkbox checked={milestone===val}></Checkbox>
            }>
              <ListItemButton onClick={()=>{setMilestone(val)}}>
                <ListItemText primary={val.name} secondary={(project.requiredMilestones.includes(val._id))?"Required":"Optional"}></ListItemText>
              </ListItemButton>
            </ListItem>
          )))}
        </List>:
        <div></div>}
      </CardContent>
      <CardActions>
        <Button
          fullWidth
          variant="contained"
          sx={{ mb: 2 }}
          onClick={()=>{
            navigate("/manage-projects")
          }}
        >
          Previous
        </Button>
        <Button
          fullWidth
          variant="contained"
          sx={{ mb: 2 }}
          onClick={()=>{
            setMilestoneToAward(milestone)
            nextStage()
          }}
        >
          Next
        </Button>
      </CardActions>

    </Card>
  )
}

const SelectUser = ({previousStage, nextStage, setUserToAward})=> {
  const {projectId} = useParams()
  const [users, setUsers] = useState([])
  useEffect(()=>{

    fetch("http://localhost:8000/enrolledUsers/"+projectId,
    {
      mode: "cors",
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": getToken(), 
        "Access-Control-Allow-Origin": "*"
      },
    })
      .then((res)=>{
        if(res.ok)
        {
          return res.json()
        }
      })
      .then((data)=>{
        setUsers(data)
      })
  },[projectId,])

  const [selectedUser, setSelectedUser] = useState({_id:0}) 
  const [searchTerm, setSearchTerm] = useState("")

  return (
    <Card sx={{width:500}}>
      <CardContent>
        <Typography component="h1" variant="h5">
          Select Users To Award
        </Typography>
        <TextField
          margin="normal"
          fullWidth
          id="Search Bar"
          label="Search"
          name="search-bar"
          value = {searchTerm}
          onChange={(e)=>{setSearchTerm(e.target.value)}}
          autoFocus
        />
        <List>
          {users.filter((u)=>(u.username.includes(searchTerm))).map((val=>(
            <ListItem key={val._id} secondaryAction={
              <Checkbox checked={selectedUser._id === val._id}></Checkbox>
            }>
              <ListItemButton onClick={()=>{
                if(selectedUser._id === val._id)
                {
                  console.log("Remove")
                  setSelectedUser(null)
                }
                else
                {
                  console.log("Add")
                  setSelectedUser(selectedUser)
                }
              }}>
                <ListItemText primary={val.username} secondary={"#"+val._id}></ListItemText>
              </ListItemButton>
            </ListItem>
          )))}
        </List>
      </CardContent>
      <CardActions>
        <Button
          fullWidth
          variant="contained"
          sx={{ mb: 2 }}
          onClick={()=>{
            previousStage()
          }}
        >
          Previous
        </Button>
        <Button
          fullWidth
          variant="contained"
          sx={{ mb: 2 }}
          onClick={()=>{
            setUserToAward(selectedUser)
            nextStage()
          }}
        >
          Next
        </Button>
      </CardActions>

    </Card>
  )
}

const SelectSupport = ({previousStage, nextStage, setAwardSupport}) => {

  const {projectId} = useParams();
  const [milestones, setMilestones] = useState([])

  useEffect(()=>{
    fetch("http://localhost:8000/projects/"+projectId,{
      mode: "cors",
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": getToken(), 
        "Access-Control-Allow-Origin": "*"
      },
    })
    .then((res)=>{
      if(res.ok)
      {
        return res.json()
      }
      else{
        throw Error()
      }
    })
    .then((data)=>{
      console.log("Project")
      console.log(data)
      setMilestones(data.milestones)
    })
  }, [projectId,])

  const [support, setSupport] = useState([]) 
  const [searchTerm, setSearchTerm] = useState("")

  return (
    <Card sx={{width:500}}>
      <CardContent>
        <Typography component="h1" variant="h5">
          Why is the User Awarded
        </Typography>
        <TextField
          margin="normal"
          fullWidth
          id="Search Bar"
          label="Search"
          name="search-bar"
          value = {searchTerm}
          onChange={(e)=>{setSearchTerm(e.target.value)}}
          autoFocus
        />
        <List>
          {milestones.filter((m)=>(m.name.includes(searchTerm))).map((val=>(
            <ListItem key={val._id} secondaryAction={
              <Checkbox checked={support.includes(val._id)}></Checkbox>
            }>
              <ListItemButton onClick={()=>{
                if(support.includes(val._id))
                {
                  console.log("Remove")
                  setSupport(support.filter(m=>(m!==val._id)))
                }
                else
                {
                  console.log("Add")
                  setSupport([...support, val._id])
                }
              }}>
                <ListItemText primary={val.name} secondary={"#"+val._id}></ListItemText>
              </ListItemButton>
            </ListItem>
          )))}
        </List>
      </CardContent>
      <CardActions>
        <Button
          fullWidth
          variant="contained"
          sx={{ mb: 2 }}
          onClick={()=>{
            previousStage()
          }}
        >
          Previous
        </Button>
        <Button
          fullWidth
          variant="contained"
          sx={{ mb: 2 }}
          onClick={()=>{
            setAwardSupport(support)
            nextStage()
          }}
        >
          Next
        </Button>
      </CardActions>

    </Card>
  )
}

