const AwardMilestones = () => {

  const {projectId} = useParams()

  const [isDrawerOpen, setDrawer] = useState(false);
  const toggleDrawer = ()=>{
    setDrawer(!isDrawerOpen)
  }

  const [stage, setStage] = useState(0);
  const nextStage = ()=>{
    setStage(stage+1);
  }
  const previousStage = ()=>{
    setStage(stage-1);
  }

  const awardUsers = () => {
    console.log("Award")
    console.log(milestoneToAward)
    console.log(userToAward)
    fetch("http://localhost:8000/awardMilestone/",
    {
      mode: "cors",
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "x-access-token": getToken(), 
        "Access-Control-Allow-Origin": "*"
      },
      body: JSON.stringify({milestoneId:milestoneToAward._id, userId: userToAward._id, data: "", support: []})
    })
  }

  const [userToAward, setUserToAward] = useState(null)
  const [milestoneToAward, setMilestoneToAward] = useState(null)
  const [awardSupport, setAwardSupport] = useState([])

  const showForm = (s,previousStage, nextStage, setMilestoneToAward)=>{
    if(s === 0)
      return (<SelectUser previousStage={previousStage} nextStage={nextStage} setUserToAward={setUserToAward}></SelectUser>)
    else if(s === 1)
      return (<SelectMilestone nextStage={nextStage} setMilestoneToAward={setMilestoneToAward} projectId={projectId} userToAward={userToAward}></SelectMilestone>)
    else if(s ===2)
      return (<SelectSupport previousStage={previousStage} nextStage={nextStage} setAwardSupport={setAwardSupport}></SelectSupport>)
    else if(s === 3)
    {
      awardUsers()
      return (<SuccessScreen></SuccessScreen>)
    }
  }

  return (
    <div>
      <MyAppBar title="Award Milestone" logoutFun={()=>{}} toggleDrawer={toggleDrawer}></MyAppBar>
      <CssBaseline></CssBaseline>
      <Drawer
          anchor={"left"}
          open={isDrawerOpen}
          onClose={toggleDrawer}
        >
          <SideMenu></SideMenu>
      </Drawer>
      <Box sx={{margin: 4,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center', 
              }}>
        {showForm(stage, previousStage, nextStage, setMilestoneToAward)}
      </Box>
    </div>
  )
}

export default AwardMilestones
