import {Box, Button, Card, CardContent, Checkbox, Container, CssBaseline, Divider, Drawer, List, ListItem, ListItemButton, ListItemIcon, ListItemText, Paper, Stack, Typography } from '@mui/material'
import AssignmentLateIcon from '@mui/icons-material/AssignmentLate';
import {React, useState} from 'react'
import {useParams} from "react-router-dom"
import MyAppBar from './MyAppBar';
import SideMenu from './SideMenu';
import { useEffect } from 'react';
import { getUser, setUser, refresh } from '../utilities/LoginService';

const ProjectScreen = ({logoutFun}) => {

  refresh()
  const {projectId} = useParams()
  const [user, setLocalUser] = useState(getUser())
  const [project, setProject] = useState({
    title: "ProjectTitle",
    creator: "-",
    date: "-", 
    milestones: []
  })

  useEffect(()=>{
    console.log(projectId)
    fetch("http://localhost:8000/projects/")
      .then((res)=>{
        if(res.ok)
        {
          console.log(res)
          return res.json()
        }
      })
      .then((data)=>{
        setProject(data.filter((p)=>(p.id == projectId))[0])
        console.log("Here")
        console.log(project)
      })
  }, [])

  const [isDrawerOpen, setDrawer] = useState(false);
  const toggleDrawer = ()=>{
    setDrawer(!isDrawerOpen)
  }

  const isMilestoneAchieved = (usr,milestone)=>{
    const userProjectIds = usr.projectsEnrolled.map((p)=>(p.projectId))
    if(!userProjectIds.includes(projectId))
      return false;
    const currentProject = usr.projectsEnrolled.filter((p)=>(p.projectId!==projectId))[0]
    return currentProject.milestonesAchieved.includes(milestone.name)

  }

  const isEnrolled = (usr,id) => {
    const projectIds = usr.projectsEnrolled.map((p)=>p.projectId)
    return projectIds.includes(id)
  }

  const enrollToProject = (id)=>{
    user.projectsEnrolled.push({
      projectId: id,
      milestonesAchieved: []
    })
    fetch("http://localhost:8000/users/"+user.id,
    {
      method: "PUT",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(user)
    })
    .then((res)=>{
      if(res.ok)
      {
        return res.json()
      }
    })
    .then((data)=>{
      setUser(data)
      setLocalUser(data)
      console.log(data)
    })
  }

  return (
    <div>
      <CssBaseline></CssBaseline>
      <MyAppBar logoutFun={logoutFun} toggleDrawer={toggleDrawer}></MyAppBar>
      <Drawer
        anchor={"left"}
        open={isDrawerOpen}
        onClose={toggleDrawer}
      >
        <SideMenu></SideMenu>
      </Drawer>
      <Box sx={{margin: 4}}>
        <Card>
          <Stack padding={4}>
            <Stack direction="row" justifyContent="space-between">
              <Typography variant='h3'>{project.title}</Typography>
              <Card>
                <CardContent>
                  <Typography variant='h5'>Details:</Typography>
                  <Divider></Divider>
                  <Stack direction="row" spacing={1}>
                    <Typography>creator:</Typography>
                    <Typography>{project.creator.username}</Typography>
                  </Stack>
                  <Stack direction="row" spacing={1}>
                    <Typography>date:</Typography>
                    <Typography>{Date(project.date)}</Typography>
                  </Stack>
                </CardContent>
              </Card>
            </Stack>
            <List>
              {project.milestones.map((milestone)=>(
                <ListItem divider={true} key={milestone.name} secondaryAction={
                  <Checkbox
                    edge="end"
                    checked={isMilestoneAchieved(user,milestone)}
                  />
                }>
                  <ListItemIcon>
                    <AssignmentLateIcon color='yellow'></AssignmentLateIcon>
                  </ListItemIcon>
                  <ListItemText primary = {milestone.name}></ListItemText>
                </ListItem>
              ))}
            </List>
            <Stack direction="row" justifyContent="end">
            <Button variant={(isEnrolled(user,projectId))?"contained":"outlined"} disabled={isEnrolled(user,projectId)} color='success' onClick={()=>{enrollToProject(projectId)}}>{(isEnrolled(user,projectId))?"Enrolled":"Enroll"}</Button>
            </Stack>
          </Stack>
        </Card>
      </Box>
    </div>
  )
}

export default ProjectScreen
